
var setUpDatabase = require('word-phoneme-map').setUpDatabase;
setUpDatabase(
  {
          dbLocation: __dirname + '/database/a-word-phoneme.db'
            },
              done
              );

function done(error) {
      if (error) {
              console.log(error)
                }
                  else {
                          console.log('Successfully set up database.');
                            }
}
